package com.lidong.suanfa.linkedlist;

public class Node {
	public int data;
	public Node next;

	public Node() {
	}

	public Node(int data) {
		// TODO Auto-generated constructor stub
		this.data = data;
		this.next = null;
	}

	public Node(int data, Node next) {
		// TODO Auto-generated constructor stub
		this.data = data;
		this.next = next;
	}

}
